class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Map<Integer,Integer> mapH=new HashMap<>();
        Map<Integer,Integer> mapV=new HashMap<>();
        for(int i: horizontalCut){
            mapH.merge(i,1,Integer::sum);
        }
        for(int i: verticalCut){
            mapV.merge(i,1,Integer::sum);
        }
        List<Integer> listH=new ArrayList<>(mapH.keySet());
        List<Integer> listV=new ArrayList<>(mapV.keySet());
        Collections.sort(listH);
        Collections.sort(listV);
        // System.out.println(listH);
        // System.out.println(listV);
        int i=listH.size()-1, j=listV.size()-1;
        long countH=0l, countV=0l, ans=0l;
        while(i>=0&&j>=0){
            if(listH.get(i)>listV.get(j)){
                ans+=(countV+1)*listH.get(i)*mapH.get(listH.get(i));
                countH+=mapH.get(listH.get(i));
                i--;                
            }
            else if(listH.get(i)<listV.get(j)){
                ans+=(countH+1)*listV.get(j)*mapV.get(listV.get(j));
                countV+=mapV.get(listV.get(j));
                j--;                
            }
            else{
                if(mapH.get(listH.get(i))>=mapV.get(listV.get(j))){
                    ans+=(countV+1)*listH.get(i)*mapH.get(listH.get(i));
                    countH+=mapH.get(listH.get(i));
                    i--;     
                }
                else{
                    ans+=(countH+1)*listV.get(j)*mapV.get(listV.get(j));
                    countV+=mapV.get(listV.get(j));
                    j--;   
                }
            } 
            // System.out.println(ans);
        }
        while(i>=0){
            ans+=(countV+1)*listH.get(i)*mapH.get(listH.get(i));
            countH+=mapH.get(listH.get(i));
            i--;  
        }
        while(j>=0){
            ans+=(countH+1)*listV.get(j)*mapV.get(listV.get(j));
            countV+=mapV.get(listV.get(j));
            j--;  
        }
        return ans;
    }
}
