class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        vector<pair<int,int>>h;
        vector<pair<int,int>>v;

        for(int i = 0; i < hor.size(); i++){
            h.push_back({hor[i], i});
        }
        for(int i = 0; i < ver.size(); i++){
            v.push_back({ver[i], i});
        }

        sort(h.begin(), h.end());
        sort(v.begin(), v.end());

        int i = h.size()-1;
        int j = v.size()-1;

        long long int ans = 0;

        int hCuts = 1;
        int vCuts = 1;


        while(i >= 0 && j >= 0){
            if(h[i].first > v[j].first){
                ans += (long long)vCuts*(long long)h[i].first;
                hCuts++;
                i--;
            }
            else{
                ans += (long long)hCuts*(long long )v[j].first;
                vCuts++;
                j--;
            }
        }

        while(i >= 0){
            ans +=( long long)vCuts*(long long)h[i].first;
                hCuts++;
                i--;
        }

        while(j >= 0){
            ans += (long long)hCuts*(long long )v[j].first;
                vCuts++;
                j--;
        }


        return ans;
    }
};
