class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        h_mult = 1
        v_mult = 1
        cuts = []
        for c in horizontalCut:
            cuts.append(('h', c))
        for c in verticalCut:
            cuts.append(('v', c))
        cuts.sort(key=lambda x: x[1])
        answer = 0
        while len(cuts) > 0:
            cut = cuts.pop()
            if cut[0] == 'h':
                answer += cut[1] * h_mult
                v_mult += 1
            else:
                answer += cut[1] * v_mult
                h_mult += 1
        return answer
