class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut.sort()
        verticalCut.sort()
        h = 1
        v = 1
        ans = 0
        while len(horizontalCut) > 0 or len(verticalCut) > 0:
            if len(horizontalCut) > 0 and not (len(verticalCut) > 0 and horizontalCut[-1] < verticalCut[-1]):
                ans += v*horizontalCut.pop(-1)
                h += 1
            else:
                ans += h*verticalCut.pop(-1)
                v += 1
        return ans
