class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) 
    {
        long long ans=0;
        long long acnt=1,bcnt=1;
        sort(a.begin(),a.end(),greater<int>{});
        sort(b.begin(),b.end(),greater<int>{});
        a.emplace_back(0);
        b.emplace_back(0);
        for(int i=0,j=0;i+1<a.size()||j+1<b.size();)
        {
            if(a[i]>b[j])
            {
                ans+=a[i]*acnt;
                bcnt++;
                i++;
            }
            else
            {
                ans+=b[j]*bcnt;
                acnt++;
                j++;
            }
        }
        return ans;
    }
};
