class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(),hc.end(),greater<int>());
            sort(vc.begin(),vc.end(),greater<int>());
            int i = 0, j = 0;
            long long int ans = 0;
            while(i<m-1 and j<n-1){
                if(hc[i]>vc[j]){
                    ans+=(hc[i]*(j+1));
                    i++;
                }else if(hc[i]<vc[j]){
                    ans+=(vc[j]*(i+1));
                    j++;
                }else{
                    if(i>j){
                        ans+=(hc[i]*(j+1));
                        i++;
                    }else{
                        ans+=(vc[j]*(i+1));
                        j++;
                    }
                }
            }
            while(i<m-1){
                ans+=(hc[i]*(j+1));
                i++;
            }
            while(j<n-1){
                ans+=(vc[j]*(i+1));
                j++;
            }
            return ans;
    }
};
