class Solution {
public:
        long long minimumCostOfBreaking(vector<int>&x, vector<int>&y, int m, int n)
{
    long long res = 0;

    sort(x.begin(),x.end(), greater<long long>());


      sort(y.begin(),y.end(), greater<long long>());



    long long hzntl = 1, vert = 1;

    int i = 0, j = 0;
    while (i < m && j < n)
    {
        if (x[i] > y[j])
        {
            res += x[i] * vert;


            hzntl++;
            i++;
        }
        else
        {
            res += y[j] * hzntl;

            vert++;
            j++;
        }
    }

    long long total = 0;
    while (i < m)
        total += x[i++];
    res += total * vert;


    total = 0;
    while (j < n)
        total += y[j++];
    res += total * hzntl;

    return res;
}
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        return  minimumCostOfBreaking(horizontalCut,verticalCut,m-1,n-1);
    }
};
