class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int, char>> c;
        for(auto cost:horizontalCut){
            c.push_back({cost,'h'});
        }
        for(auto cost:verticalCut){
            c.push_back({cost,'v'});
        }
        sort(c.begin(), c.end(),greater<pair<int,int>>());
        int hp = 1, vp = 1;
        long long totalCost = 0;
        for (auto &[cost, type]:c) {
            if (type == 'h') {
                totalCost += 1ll*(cost)*vp;
                hp++;
            } else {
                totalCost += 1ll*(cost)*hp;
                vp++;
            }
        }

        return totalCost;
    }
};
