class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(),horizontalCut.end());
        sort(verticalCut.begin(),verticalCut.end());
        long long total=0;
        int hcuts=1;
        int vcuts=1;
        int vp=verticalCut.size()-1;
        int hp=horizontalCut.size()-1;
        while(vp>=0 && hp>=0){
            if(verticalCut[vp]>horizontalCut[hp]){
                // cout<<verticalCut[vp]<<" "<<hcuts<<endl;
                total+=verticalCut[vp]*hcuts*1LL;
                vcuts++;
                vp--;
            }
            else{
                // cout<<horizontalCut[hp]<<" "<<vcuts<<endl;
                total+=horizontalCut[hp]*vcuts*1LL;
                hcuts++;
                hp--;
            }
        }
        while (vp >= 0) {
            total += verticalCut[vp] * hcuts;
            vp--;
        }
        while (hp >= 0) {
            total += horizontalCut[hp] * vcuts;
            hp--;
        }
        return total;
    }
};
