class Solution:
    def minimumCost(self, m: int, n: int, hc: List[int], vc: List[int]) -> int:
        l = []
        for i in hc:
            l.append((i, 1))

        for i in vc:
            l.append((i, 0)) 

        l.sort(reverse=True, key=lambda x: x[0])

        h = v = 0
        mcost = 0

        for val, horv in l:
            if horv == 1: 
                mcost += val * (v + 1) 
                h += 1
            else: 
                mcost += val * (h + 1)
                v += 1

        return mcost
