class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        priority_queue<pair<int,char>>pq;

        for(auto i:vc){
            pq.push({i,'y'});
        }
        for(auto i:hc){
            pq.push({i,'x'});
        }


        int h=1,v=1;
        long long ans=0;
        while(!pq.empty()){
            auto it=pq.top();
            pq.pop();
            if(it.second=='y'){
                long long val=h;
                val*=it.first;
                v++;
                ans+=val;
            }
            else{
                long long val=v;
                val*=it.first;
                h++;
                ans+=val;
            }
        }
        return ans;

    }
};
