class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        h, v = 1, 1
        horizontalCut.sort()
        verticalCut.sort()
        res = 0
        while h + v < m + n:
            if not horizontalCut:
                v += 1
                res += verticalCut.pop() * h
            elif not verticalCut:
                h += 1
                res += horizontalCut.pop() * v
            else:
                if horizontalCut[-1] >= verticalCut[-1]:
                    h += 1
                    res += horizontalCut.pop() * v
                else:
                    v += 1
                    res += verticalCut.pop() * h
        return res
