class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:

        combined = []

        #horizontal == True

        for horizontal in horizontalCut:
            combined.append((horizontal, True))
        for vertical in verticalCut:
            combined.append((vertical, False))

        combined.sort(reverse = True)

        #print(combined)

        horizontalPieces = 1
        verticalPieces = 1
        total = 0
        for cut, isHorizontal in combined:

            if isHorizontal == True:
                verticalPieces += 1
                total += cut * horizontalPieces
            else:
                horizontalPieces += 1
                total += cut*verticalPieces

        return total
