class Solution
{
public:
    long long minimumCost(int m, int n, vector<int> &horizontalCut, vector<int> &verticalCut)
    {
        sort(horizontalCut.begin(), horizontalCut.end());
        sort(verticalCut.begin(), verticalCut.end());
        reverse(horizontalCut.begin(), horizontalCut.end());
        reverse(verticalCut.begin(), verticalCut.end());

        long long ans = 0;
        int h = 1, v = 1, h_ind = 0, v_ind = 0;

        while (horizontalCut.size() > h_ind and verticalCut.size() > v_ind)
        {
            if (horizontalCut[h_ind] < verticalCut[v_ind])
            {
                ans += (long long)verticalCut[v_ind] * h;
                v++, v_ind++;
            }
            else
            {
                ans += (long long)horizontalCut[h_ind] * v;
                h++, h_ind++;
            }
        }

        while (verticalCut.size() > v_ind)
        {
            ans += (long long)(verticalCut[v_ind] * h);
            v++, v_ind++;
        }

        while (horizontalCut.size() > h_ind)
        {
            ans += (long long)(horizontalCut[h_ind] * v);
            h++, h_ind++;
        }

        return ans;
    }
};
