class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {

        int row = 1, col = 1;
        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());

        long long cost = 0;
        int i = 0, j = 0;

        while(i < m-1 || j < n-1){
            if(i == m-1){
                // row done
                cost += row * v[j];
                col++;
                j++;
            }
            else if(j == n-1){
                // col done
                cost += col*h[i];
                row++;
                i++;
            }
            else{
                if(h[i] > v[j]){
                    cost += col*h[i];
                    row++;
                    i++;
                }
                else{
                    cost += row * v[j];
                    col++;
                    j++;
                }
            }
        }

        // cout << cost << endl;
        return cost;

    }
};
