class Solution {
public:
    long long  minimumCost(int m, int n, vector<int>& h, vector<int>& ve) {
        vector<vector<int>> v1 , v2  ;

        rep( i , m - 1 )
        {
            v1.push_back({ h[i] , 0 }) ;
        }
        rep( i , n - 1 )
        {
            v2.pb({ ve[i] , 1 }) ;
        }
        sort(all(v1)) ; sort(all(v2)) ;

        ll r = 1 , c = 1 ,   res = 0  ;

        for ( int i = si(v1) - 1 , j = si(v2) - 1   ; i >= 0 || j >= 0 ;  )
        {
            if ( j < 0 || ( i >= 0 && v1[i][0] >= v2[j][0] ) )
            {
                auto &a = v1[i] ;
                int t = a[1] ;
                ll cost = a[0]  ;
                if ( t == 1 )
                {
                    res += 1LL * cost * r ; c++ ;
                }
                else {
                    res += 1LL * cost * c ; r++ ;
                }
                i-- ;
            }
            else  {
                if( j < 0 ) break ; 
                auto &a = v2[j] ;
                int t = a[1] ;
                ll cost = a[0]  ;
                if ( t == 1 )
                {
                    res += 1LL * cost * r ; c++ ;
                }
                else {
                    res += 1LL * cost * c ; r++ ;
                }
                j-- ;
            }
        }
        return res ;
    }
};
