class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        priority_queue<pair<long long,char>> pq;
       for(auto i : hc) pq.push({i,'h'});
       for(auto i : vc) pq.push({i,'v'});
       long long v=0,h=0;
       long long res=0;
       while(pq.size()){
            auto [a,b]=pq.top();
            pq.pop();
            if(b=='h'){
                res+=(a*(v+1));
                h++;
            }
            else{
                res+=(a*(h+1));
                v++;
            }
       }
        return res;
    }
};
