class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        int h = 0, v = 0;
        priority_queue<pair<int,int>> pq;
        for(int i = 0;i<m-1;i++){
            pq.push({horizontalCut[i],i});
        }
        for(int i = 0;i<n-1;i++){
            pq.push({verticalCut[i],m+i});
        }
        long long ans = 0;
        while(!pq.empty()){
            if(pq.top().second < m-1){
                ans += (v+1)*1ll*pq.top().first;
                h++;
            }
            else{
                ans += (h+1)*1ll*pq.top().first;
                v++;
            }
            pq.pop();
        }
        return ans;
    }
};
