class Solution {
    public long minimumCost(int m, int n, int[] hcut, int[] vcut) {
        Arrays.sort(hcut);
        Arrays.sort(vcut);

        int ind1 = m-2;
        int ind2 = n-2;

        int cnt1 = 1;
        int cnt2 = 1;
        long sum = 0;

        while(ind1>=0 && ind2>=0){

            if(hcut[ind1] > vcut[ind2]){
                cnt1++;
                sum += (long)cnt2*(long)hcut[ind1];
                ind1--;
            }
            else{
                cnt2++;
                sum += (long)cnt1*(long)vcut[ind2];
                ind2--;
            }
        }
        while(ind1>=0){
            sum += (long)cnt2*(long)hcut[ind1];
            ind1--;
        }
        while(ind2>=0){
            sum += (long)cnt1*(long)vcut[ind2];
            ind2--;
        }
        return sum;
    }
}
