class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
          priority_queue<long long>h,v;
      for(auto it:horizontalCut) h.push(it);
        for(auto it:verticalCut) v.push(it);
long long hc=1;
long long vc=1;
long long cost=0;
        while(!h.empty() && !v.empty()){
            if(h.top()>=v.top()){
             cost+=h.top()*vc;
             h.pop();
             hc++;
            }else{
                cost+=v.top()*hc;
              v.pop();
              vc++;

            }
        }
        while(!h.empty()){
            cost+=h.top()*vc;
            h.pop();
            hc++;
        }
        while(!v.empty()){
            cost+=v.top()*hc;
            v.pop();
            vc++;
        }
        return cost;

    }
};
