class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<int,int>>pq;
        for(auto it:horizontalCut)pq.push({it,1});
        for(auto it:verticalCut)pq.push({it,2});

        long long c1=1,c2=1,ans=0;
        while(!pq.empty()){
            auto it=pq.top();
            pq.pop();
            if(it.second==1){
                ans+=it.first*c1;
                c2++;
            }
            else{
                ans+=it.first*c2;
                c1++;
            }
        }
        return ans;
    }
};
