class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        long h = 1, v =1,cost=0;
        //System.out.println(m+" "+horizontalCut.length);
        m-=2;n-=2;
        while(m>=0&&n>=0){
            //System.out.println(horizontalCut[m]+" "+verticalCut[n]);
            if(horizontalCut[m]>verticalCut[n]){
                cost += (long)horizontalCut[m]*v;
                m--;h++;
            } else{
                cost += (long)verticalCut[n]*h;
                n--;v++;
            }
        }
        //System.out.println(cost);
        while(m>=0){
            cost += (long)horizontalCut[m]*v;
            m--;h++;
        }
        while(n>=0){
            cost += (long)verticalCut[n]*h;
            n--;v++;
        }
        return cost;
    }
}
