class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        int xcuts = 1;
        int ycuts = 1;

        sort(h.begin() , h.end());
        sort(v.begin() , v.end());

        long long ans = 0;

        int x = m-2;
        int y = n-2;

        while( x >= 0 && y>=0 )
        {
            if( h[x] > v[y] )
            {
                //cout <<" cut along " << h[x] << "with " << ycuts << endl;
                ans = ans + (ycuts*h[x]);
                x--;
                xcuts++;
            }
            else
            {
                //cout <<" cut along " << v[y] << "with " << xcuts << endl;
                ans = ans + (xcuts*v[y]);
                y--;
                ycuts++;
            }
        }

        while( x >= 0)
        {
            //cout <<" cut along " << h[x] << "with " << ycuts << endl;
            ans = ans + (ycuts*h[x]);
            x--;
            xcuts++;
        }

        while( y >= 0 )
        {
            //cout <<" cut along " << v[y] << "with " << xcuts << endl;
            ans = ans + (xcuts*v[y]);
            y--;
            ycuts++;
        }

        return ans;
    }
};
