class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);

        long ans = 0;
        int h = 1, v = 1;
        int a = horizontalCut.length-1, b = verticalCut.length-1;

        while(a>=0 || b>=0) {
            if(b<0 || (a>=0 && horizontalCut[a] > verticalCut[b])) {
                ans += (long)horizontalCut[a--] * v;
                h++;
            } else {
                ans += (long)verticalCut[b--] * h;
                v++;
            }
        }

        return ans;
    }
}
