class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        int hs=1;
        int vs=1;
        long long ans=0;

        priority_queue<int> pqhor;
        priority_queue<int> pqver;

        for(int i=0;i<horizontalCut.size();i++){
            pqhor.push(horizontalCut[i]);
        }

        for(int i=0;i<verticalCut.size();i++){
            pqver.push(verticalCut[i]);
        }

        while(!pqhor.empty() and !pqver.empty()){
            if(pqhor.top()>pqver.top()){
                ans+=(pqhor.top()*vs);
                hs++;
                pqhor.pop();
            }
            else{
                ans+=(pqver.top()*hs);
                vs++;
                pqver.pop();
            }
        }

        while(!pqhor.empty()){
            ans+=(pqhor.top()*vs);
            hs++;
            pqhor.pop();
        }

        while(!pqver.empty()){
            ans+=(pqver.top()*hs);
            vs++;
            pqver.pop();
        }

        return ans;
    }
};
