class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        h = [(hc, 'h') for hc in horizontalCut]
        h += [(vc, 'v') for vc in verticalCut]

        h.sort(reverse=True)

        h_p = 1
        v_p = 1
        total = 0

        for c, t in h:
            if t == 'h':
                total += c * v_p
                h_p += 1
            elif t == 'v':
                total += c * h_p
                v_p += 1
        return total
