class Solution {
#define m 1000000007
#define ll long long

public:
    long long minimumCost(int ro, int n, vector<int>& x, vector<int>& y) {
        sort(x.begin(),x.end(),greater<int>());
        sort(y.begin(),y.end(),greater<int>());
        ll h=1;
        ll v=1;
        ll i=0;
        ll j=0;
        ll tot=0;
        while(i<ro-1 && j<n-1)
        {
            if(x[i]>y[j]) 
            { 
            tot=(tot+(x[i] * v)); 


            h++; 
            i++; 
            } 
            else
            { 
             tot = (tot+(y[j] * h)); 


             v++;
             j++; 
            } 
        }
        ll res=0;
        while (i<ro-1) 
        res+=x[i++]; 
        tot= (tot+(res*v)); 


         res=0;
         while (j < n-1) 
         res += y[j++]; 
         tot=(tot+(res*h));
        return tot;
    }

};
