class Solution {
public:
    using ll = long long;
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<ll, ll>> a;
        for (int i : h) {
            a.emplace_back(i, 0);
        }
        for (int j : v) {
            a.emplace_back(j, 1);
        }
        sort(a.begin(), a.end());
        std::reverse(a.begin(), a.end());
        ll h1 = 1, v1 = 1;
        ll ans = 0;
        for (const auto [c, t] : a) {
            if (t == 0) {
                // horizontal cut.
                ans += c * h1;
                v1++;
            } else {
                // vertical cut.
                ans += c * v1;
                h1++;
            }
        }
        return ans;
    }
};
