func helper(m int, n int, hc []int, vc []int) int64 {
    if len(hc) == 0 && len(vc) == 0 {
        return int64(0)
    }
    if len(hc) == 0 {
        return int64(vc[0]) + helper(m, n-1, hc, vc[1:])
    }
    if len(vc) == 0 {
        return int64(hc[0]) + helper(m-1, n, hc[1:], vc)
    }
    if hc[0] < vc[0] {
        return int64(n)*int64(hc[0]) + helper(m-1, n, hc[1:], vc)
    }
    return int64(m)*int64(vc[0]) + helper(m, n-1, hc, vc[1:])
}

func minimumCost(m int, n int, horizontalCut []int, verticalCut []int) int64 {
    sort.Ints(horizontalCut)
