class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());
        long long i = 0, j = 0, res = 0;
        while (i < n - 1 && j < m - 1) {
            if (v[i] > h[j]) {
                res += (v[i] * (j + 1));
                i++;
            }
            else if (v[i] < h[j]) {
                res += (h[j] * (i + 1));
                j++;
            }
            else {
                if (i < j) {
                    res += (h[j] * (i + 1));
                    j++;
                }
                else {
                    res += (v[i] * (j + 1));
                    i++;
                }
            }
        }
        while (i < n - 1) {
            res += (v[i] * (j + 1));
            i++;
        }
        while (j < m - 1) {
            res += (h[j] * (i + 1));
            j++;
        }
        return res;
    }
};
