class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long,int>>req;
        for(auto x:horizontalCut)req.push_back({x,0});
        for(auto x:verticalCut)req.push_back({x,1});
        sort(req.rbegin(),req.rend());
        long long h = 0, v=0,ans = 0;
        //reverse(re.begin(),x.end());
        for(auto x:req){
            if(x.second){
                v++;
                ans+=(h+1)*(x.first);
            }
            else{
                h++;
                ans+=(v+1)*(x.first);
            }
        }
        return ans;
    }
};
