class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        ans = 0
        row = col = 1

        t1 = [(v, 0) for v in horizontalCut]
        t2 = [(v, 1) for v in verticalCut]
        nums = sorted(t1 + t2, reverse=True)

        for v, op in nums:
            if op == 0:
                ans += col * v
                row += 1
            else:
                ans += row * v
                col += 1

        return ans
