class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans=0;
        sort(h.begin(),h.end(),greater<int>());
        sort(v.begin(),v.end(),greater<int>());
        int i=0,j=0;
        int ch=1,cv=1;
        while(i<m-1 && j<n-1){
            if(h[i]>=v[j]){
                ans+=h[i]*ch;
                i++;
                cv++;
            }
            else{
                ans+=v[j]*cv;
                j++;
                ch++;
            }
        }
        while(i<m-1){
            ans+=h[i]*ch;
            i++;
        }
        while(j<n-1){
            ans+=v[j]*cv;
            j++;
        }
        return ans;
    }
};
