#define ll long long
#define MOD 998244353
#define MOD2 1000000007
#define vt vector
struct pii {int x, y;};
struct pll {ll x, y;};

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end());
        ll sh = 0, sv = 0;
        for (int i = 0; i < m-1; i++) sh+=h[i];
        for (int i = 0; i < n-1; i++) sv+=v[i];
        if (m==1) return sv;
        if (n==1) return sh;
        vt<ll> pre(m-1);
        pre[0]=h[0];
        for (int i = 1; i < m-1; i++) {
            pre[i] = pre[i-1]+h[i];
        }
        ll ans = sh*n+sv*m;
        for (int i = 0; i < n-1; i++) {
            auto it = lower_bound(h.begin(), h.end(), v[i]);
            int idx = it-h.begin();
            if (idx < m-1) {
                ans -= pre[m-2]-(idx>0?pre[idx-1]:0);
            }
            ans -= idx*v[i];
        }
        return ans;
    }
};
