#define ll long long 
class Solution {
public:
    ll minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        ll horizontalPieces = 1, verticalPieces = 1;
        ll totalCost = 0, i = 0, j = 0;

        sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());
        sort(verticalCut.begin(), verticalCut.end(), greater<int>());

        while (i < horizontalCut.size() && j < verticalCut.size()) {
            if (horizontalCut[i] >= verticalCut[j]) {
                totalCost += horizontalCut[i] * verticalPieces;
                ++horizontalPieces;
                ++i;
            } else if(horizontalCut[i] < verticalCut[j]){
                totalCost += verticalCut[j] * horizontalPieces;
                ++verticalPieces;
                ++j;
            }
        }

        while (j < verticalCut.size()) {
            totalCost += verticalCut[j] * horizontalPieces;
            ++verticalPieces;
            ++j;
        }

        while (i < horizontalCut.size()) {
            totalCost += horizontalCut[i] * verticalPieces;
            ++horizontalPieces;
            ++i;
        }

        return totalCost;
    }
};
