class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
         sort(a.rbegin(), a.rend());
        sort(b.rbegin(), b.rend());


        long long tc = 0;
         long long h = 0, v = 0;
        long long hp = 1, vp = 1;
        vector<int> p(n);
        vector<int> q(m);
        sort(p.begin(),p.end());
        while (h < a.size() && v < b.size()) {
            if (a[h] < b[v]) {
                 tc += b[v] * hp;
                 v++;
               vp++;

            } else {
                 tc += a[h] * vp;
                h++;
                hp++;
            }
        }


        while (h < a.size()) {
            tc += a[h] * vp;
            h++;
        }


        while (v < b.size()) {
            tc += b[v] * hp;
            v++;
        }

        return tc;
    }
};
