class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        int hc = 1;
        int vc = 1;
        //h -> 0,v -> 1
        priority_queue<pair<int,int>> pq;
        for(auto& c : horizontalCut)
            pq.push({c,0});
        for(auto& c : verticalCut)
            pq.push({c,1});
        pair<int,int> p;
        long long res = 0;
        while(!pq.empty()){
            p = pq.top();
            pq.pop();
            if(p.second)
                hc++;
            else
                vc++;
            res+=(p.second?p.first*vc:p.first*hc);
        } 
        return res;
    }
};
