class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        priority_queue<long long> pqr, pqc;
        for (long long i : h)
            pqr.push(i);
        for (long long i : v)
            pqc.push(i);

        long long ans = 0;
        long long R = 0, C = 0;
        while (pqr.size() or pqc.size()) {
            if (pqr.empty()) {
                ans += (R + 1) * (pqc.top());
                pqc.pop();
                C++;
            } else if (pqc.empty()) {
                ans += (C + 1) * (pqr.top());
                pqr.pop();
                R++;
            } else {
                int x = (pqc.top()), y = (pqr.top());
                //cout << x << " " << y << '\n';
                if (x >= y) {
                    ans += x * (R + 1);
                    pqc.pop();
                    C++;
                } else {
                    ans += y * (C + 1);
                    pqr.pop();
                    R++;
                }
            }
        }
        return ans;
    }
};
