class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int, int>> cuts;
        for (int cost : v) {
            cuts.push_back({cost, 1});
        }
        for (int cost : h) {
            cuts.push_back({cost, -1});
        }
        sort(cuts.rbegin(), cuts.rend());
        int a=1;
        int b=1;
        long long ans=0;
        for (auto& cut : cuts) {
            if (cut.second == -1) {
                ans += static_cast<long long>(cut.first)*b;
                a++;
            } else {
                ans += static_cast<long long>(cut.first)*a;
                b++;
            }
        }
        return ans;
    }
};
