class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        auto h=horizontalCut;
        auto v=verticalCut;
        sort(h.begin(),h.end());
        reverse(h.begin(),h.end());
        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());
        long long ans=0,hh=0,vv=0;
        for(auto it:h)ans+=it,hh+=it;
        for(auto it:v)ans+=it,vv+=it;
        int hl=0,vl=0;
        while(hl!=h.size()&&vl!=v.size()){
            if(h[hl]<v[vl]){
                ans+=hh;
                vv-=v[vl];
                vl++;
            }
            else{
                ans+=vv;
                hh-=h[hl];hl++;
            }
        }
        return ans;
    }
};
