class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        total_cost = 0
        h_cs = [(cut, 'H') for cut in horizontalCut]
        v_cs = [(cut, 'V') for cut in verticalCut]
        v_count = h_count = 0
        for cut_cost, dimension in sorted(h_cs + v_cs, reverse=True):
            if dimension == 'H':
                h_count += 1
                total_cost += cut_cost * (v_count + 1)
            else:
                v_count += 1
                total_cost += cut_cost * (h_count + 1)
        return total_cost
