class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>>arr;
        for(int i:horizontalCut)arr.push_back(pair(i,0));
        for(int i:verticalCut)arr.push_back(pair(i,1));
        sort(arr.begin(),arr.end(),greater<pair<int,int>>());
        long long res=0;
        int hori=1,verti=1;
        for(auto [x,y]:arr){
            if(y==0){
                res+=x*verti;
                hori++;
            }else{
                res+=x*hori;
                verti++;
            }
        }
        return res;
    }
};
