class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
          vector<pair<int, int>> vp;
        for (int i = 0; i < m - 1; ++i) {
            vp.push_back({horizontalCut[i], 0});
        }
        for (int j = 0; j < n - 1; ++j) {
            vp.push_back({verticalCut[j], 1});
        }

        sort(vp.rbegin(), vp.rend());

        long long h = 1;
        long long v = 1;
        long long ans = 0;


        for (auto& it : vp) {
            if (it.second == 0) {
                ans += it.first * v;
                h++;
            } else {
                ans += it.first * h;
                v++;
            }
        }

        return ans;
    }
};
