class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.rbegin(), hc.rend());
        sort(vc.rbegin(), vc.rend());

        long long i=0, j=0, ans=0;
        while(i<hc.size() && j<vc.size()){
            if(hc[i] > vc[j]){
                ans += (long long)hc[i]*(j+1);
                i++;
            }else{
                ans += (long long)vc[j]*(i+1);
                j++;
            }
        }

        while(i<hc.size()){
            ans += (long long)hc[i++]*(j+1);
        }

        while(j<vc.size()){
            ans += (long long)vc[j++]*(i+1);
        }

        return ans;
    }
};
