class Solution {
public:
    struct Cut {
    int cost;
    bool isHorizontal;
    Cut(int c, bool h) : cost(c), isHorizontal(h) {}
};

bool static cmp(const Cut& a, const Cut& b) {
    return a.cost > b.cost;
}

long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
    vector<Cut> cuts;

    for (int cost : horizontalCut) {
        cuts.push_back(Cut(cost, true));
    }

    for (int cost : verticalCut) {
        cuts.push_back(Cut(cost, false));
    }

    sort(cuts.begin(), cuts.end(), cmp);

    int hSegments = 1, vSegments = 1;
    long long ans = 0;

    for (const Cut& cut : cuts) {
        if (cut.isHorizontal) {
            ans += cut.cost * vSegments;
            hSegments++;
        } else {
            ans += cut.cost * hSegments;
            vSegments++;
        }
    }

    return ans;
}
};
