class Solution {
public:
    long long minimumCost(int M, int N, vector<int>& X, vector<int>& Y) {
        sort(X.rbegin(), X.rend());
        sort(Y.rbegin(), Y.rend());
        int i=0, j=0;
        int hz=1, vt=1;
        long long ans=0;
        while(i<M-1 && j<N-1) {
            if(X[i] > Y[j]) {
                // vertical cut
                ans += (hz*X[i]);
                vt++; i++;
            }
            else {
                // horizontal cut
                ans += (vt*Y[j]);
                hz++; j++;
            }
        }
        while(i<M-1) {
            ans += (hz*X[i]);
            vt++; i++;
        }
        while(j<N-1) {
            ans += (vt*Y[j]);
            hz++; j++;
        }
        return ans;
    }
};
