class Solution:
    def minimumCost(self, m: int, n: int, hCut: List[int], vCut: List[int]) -> int:
        hCut.sort(reverse=True)
        vCut.sort(reverse=True)
        totalCost = 0
        hCuts = 0
        vCuts = 0
        while hCuts < len(hCut) or vCuts < len(vCut):
            if hCuts < len(hCut) and (vCuts >= len(vCut) or hCut[hCuts] >= vCut[vCuts]):
                totalCost += hCut[hCuts] * (vCuts + 1)
                hCuts += 1
            else:
                totalCost += vCut[vCuts] * (hCuts + 1)
                vCuts += 1
        return totalCost
