#define ll long long

class Solution {
public:
    ll minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<ll,char>> arr;
        for (auto &x: horizontalCut)
            arr.push_back(make_pair(x, 'h'));
        for (auto &x: verticalCut)
            arr.push_back(make_pair(x, 'v'));

        sort(arr.rbegin(), arr.rend());

        ll h_cuts = 1, v_cuts = 1, res = 0;
        for (auto &[cost, dir]: arr) {
            if (dir == 'h') {
                res += (cost * v_cuts);
                ++h_cuts;
            } else {
                res += (cost * h_cuts);
                ++v_cuts;
            }
        }
        return res;
    }
};
