class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cost = []
        for c in horizontalCut:
            cost.append((c, 0))
        for c in verticalCut:
            cost.append((c, 1))
        times = [1, 1]
        cost.sort(reverse=True)
        ans = 0
        for c, t in cost:
            ans += c * times[t]
            times[t ^ 1] += 1
        return ans
