class Solution:
    def minimumCost(self, m, n, hor_costs, ver_costs):
        cuts = [(cost, 'h') for cost in hor_costs] + [(cost, 'v') for cost in ver_costs]
        cuts.sort(reverse=True)
        multi = {'h':1, 'v':1}

        result = 0
        for cost, direction in cuts:
            result += cost * multi[direction]
            other_direction = 'h' if direction=='v' else 'v'
            multi[other_direction] += 1

        return result
